// App.js
import React, { useState, useEffect } from 'react';
import BotsPage from './components/BotsPage';

function App() {
  // ...

  const handleDelete = (botId) => {
    fetch(`http://localhost:8001/bots/${botId}`, {
      method: 'DELETE',
    })
      .then(() => {
        // Remove the deleted bot from the enlistedBots state
        setEnlistedBots((prevEnlistedBots) =>
          prevEnlistedBots.filter((bot) => bot.id !== botId)
        );
      })
      .catch((error) => console.error('Error deleting bot:', error));
  };

  return (
    <div>
      <h1>Welcome to Bot Battlr</h1>
      <BotsPage onEnlist={handleEnlist} enlistedBots={enlistedBots} onDelete={handleDelete} />
    </div>
  );
}

export default App;
