import React from 'react';

const Bot = ({ bot, onEnlist, onRemove, onShowSpecs, onDischarge, enlisted }) => {
  const handleEnlist = () => {
    onEnlist(bot);
  };

  const handleShowSpecs = () => {
    onShowSpecs(bot);
