import React from 'react';
import Bot from './Bot';

const BotCollection = ({ data, enlistedBots, onEnlist, onDelete, onShowBotSpecs }) => {
  if (!data || data.length === 0) {
    // If data is empty or undefined, display a loading message or fallback content
    return <p>Loading...</p>;
  }

  return (
    <div className=''>
      {/* Render the Bot components using the map function */}
      {data.map((bot) => (
        <Bot
          key={bot.id}
          bot={bot}
          onEnlist={onEnlist}
          onDelete={onDelete}
          onShowSpecs={() => onShowBotSpecs(bot)}
          enlisted={enlistedBots.some((enlistedBot) => enlistedBot.id === bot.id)}
        />
      ))}
    </div>
  );
};

export default BotCollection;
