// components/YourBotArmy.js
import React, { useState } from 'react';

const YourBotArmy = ({ enlistedBots, onDelete }) => {
  const [armyBots, setArmyBots] = useState([]); // Rename the state variable to "armyBots"

  // ...

  const handleEnlist = (bot) => {
    // Check if bot_class already enlisted
    if (armyBots.some((b) => b.bot_class === bot.bot_class)) {
      alert(`You can only enlist one bot per class (${bot.bot_class}).`);
    } else {
      setArmyBots((prevArmyBots) => [...prevArmyBots, bot]);
    }
  };

  // ...

  return (
    <div className="your-bot-army">
      {/* ... */}
    </div>
  );
};

export default YourBotArmy;
