// YourBotArmy.js
import React from 'react';
import Bot from './Bot';
<h2>Your Bot Army</h2>
const YourBotArmy = ({ enlistedBots = [], onDelete }) => {
  return (

    <div className="your-bot-army">
      <h2>Your Bot Army</h2>
      {enlistedBots.length === 0 ? (
        <p>No bots enlisted in your army.</p>
      ) : (
        enlistedBots.map((bot) => (
          <Bot
            key={bot.id}
            bot={bot}
            onEnlist={() => {}} // We can leave these as empty functions since we don't need them here
            onDelete={onDelete} // Pass the onDelete prop to Bot component
            onShowSpecs={() => {}} // We can leave these as empty functions since we don't need them here
            enlisted
          />
        ))
      )}
    </div>
  );
};

export default YourBotArmy;
