// App.js
import React, { useState, useEffect } from 'react';
import YourBotArmy from './YourBotArmy';
import BotCollection from './BotCollection';
import SortBar from './SortBar';
import BotSpecs from './BotSpecs';
import FilterBar from './FilterBar'; // Import the FilterBar component
import './App.css';

function App() {
  const [data, setData] = useState([]);
  const [enlistedBots, setEnlistedBots] = useState([]);
  const [showBotSpecs, setShowBotSpecs] = useState(false);
  const [selectedBot, setSelectedBot] = useState(null);
  const [filteredData, setFilteredData] = useState([]);
  const [enlistedClasses, setEnlistedClasses] = useState([]); // Keep track of enlisted bot classes

  useEffect(() => {
    fetch('http://localhost:8001/bots')
      .then((response) => response.json())
      .then((data) => {
        setData(data);
        setFilteredData(data); // Initialize filteredData with all the data
      })
      .catch((error) => console.error('Error fetching bots:', error));
  }, []);

  const handleEnlist = (bot) => {
    const botClass = bot.bot_class;
    if (!enlistedClasses.includes(botClass)) {
      // Only enlist if the bot's class is not already enlisted
      setEnlistedBots((prevEnlistedBots) => [...prevEnlistedBots, bot]);
      setEnlistedClasses((prevEnlistedClasses) => [...prevEnlistedClasses, botClass]);
    }
  };

  const handleRemove = (botId) => {
    setEnlistedBots((prevEnlistedBots) =>
      prevEnlistedBots.filter((bot) => bot.id !== botId)
    );
    setEnlistedClasses((prevEnlistedClasses) => {
      // Remove the class of the bot being removed from the enlisted classes
      const botClass = data.find((bot) => bot.id === botId)?.bot_class;
      return prevEnlistedClasses.filter((className) => className !== botClass);
    });
  };

  const handleShowBotSpecs = (bot) => {
    setSelectedBot(bot);
    setShowBotSpecs(true);
  };

  const handleHideBotSpecs = () => {
    setShowBotSpecs(false);
  };

  // Define a function to discharge a bot
  const handleDischarge = (botId) => {
    // Remove the bot from the enlistedBots state
    setEnlistedBots((prevEnlistedBots) =>
      prevEnlistedBots.filter((bot) => bot.id !== botId)
    );

    // Remove the bot from the backend using fetch with DELETE method
    fetch(`http://localhost:8001/bots/${botId}`, {
      method: 'DELETE',
    })
      .then((response) => response.json())
      .then((data) => console.log('Bot discharged:', data))
      .catch((error) => console.error('Error discharging bot:', error));
  };

  // Function to filter data based on selected classes
  const applyFilters = (selectedClasses) => {
    if (selectedClasses.length === 0) {
      setFilteredData(data); // No filters selected, show all data
    } else {
      setFilteredData(data.filter((bot) => selectedClasses.includes(bot.bot_class)));
    }
  };

  useEffect(() => {
    applyFilters([]); // Initial data, no filters
  }, [data]);

  return (
    <div>
      <h1>Welcome to Bot Battlr</h1>
      {!showBotSpecs && <SortBar data={filteredData} setData={setFilteredData} />}
      <FilterBar setFilters={applyFilters} /> {/* Pass the applyFilters function as a prop */}
      <YourBotArmy
        enlistedBots={enlistedBots}
        onDelete={handleRemove}
        onDischarge={handleDischarge} // Pass the handleDischarge function as a prop
      />
      {showBotSpecs ? (
        <BotSpecs bot={selectedBot} onEnlist={handleEnlist} onBack={handleHideBotSpecs} />
      ) : (
        <BotCollection
          data={filteredData} // Use the filteredData instead of the original data
          enlistedBots={enlistedBots}
          onEnlist={handleEnlist}
          onRemove={handleRemove}
          onShowSpecs={handleShowBotSpecs}
          onDischarge={handleDischarge} // Pass the handleDischarge function as a prop
        />
      )}
    </div>
  );
}

export default App;
