// SortBar.js
import React from 'react';

const SortBar = ({ data, setData }) => {
  const handleSort = (property) => {
    const sortedData = [...data].sort((a, b) => {
      if (typeof a[property] === 'number' && typeof b[property] === 'number') {
        // If both values are numbers, sort numerically
        return a[property] - b[property];
      } else if (typeof a[property] === 'string' && typeof b[property] === 'string') {
        // If both values are strings, sort alphabetically
        return a[property].localeCompare(b[property]);
      } else {
        // For other types (e.g., booleans), maintain the original order
        return 0;
      }
    });

    setData(sortedData);
  };

  return (
    <div className="sort-bar">
      <button onClick={() => handleSort('health')}>Sort by Health</button>
      <button onClick={() => handleSort('damage')}>Sort by Damage</button>
      <button onClick={() => handleSort('armor')}>Sort by Armor</button>
      <h2>YOUR BOT ARMY</h2>
    </div>
  );
};

export default SortBar;
