import React from 'react';

const Bot = ({ bot, onEnlist, onDelete, onShowSpecs, onDischarge, enlisted }) => {
  const { id, name, health, damage, armor, bot_class, catchphrase, avatar_url } = bot;

  const handleEnlist = () => {
    onEnlist(bot);
  };

  const handleDelete = () => {
    onDelete(id);
  };

  const handleShowSpecs = () => {
    onShowSpecs(bot);
  };

  const handleDischarge = () => {
    onDischarge(id);
    fetch(`http://localhost:8001/bots/${id}`, {
      method: 'DELETE',
    })
      .then((response) => response.json())
      .then((data) => console.log('Bot discharged:', data))
      .catch((error) => console.error('Error discharging bot:', error));
  };

  return (
    <div className="bot-card">
      <img src={avatar_url} alt={name} />
      <h3>{name}</h3>
      <p>Health: {health}</p>
      <p>Damage: {damage}</p>
      <p>Armor: {armor}</p>
      <p>Class: {bot_class}</p>
      <p>Catchphrase: {catchphrase}</p>
      {enlisted ? (
        <>
          <button onClick={handleShowSpecs}>Show Specs</button>
          <button onClick={handleDischarge} style={{ color: 'red' }}>
            Remove
          </button>
        </>
      ) : (
        <>
          <button onClick={handleEnlist}>Enlist</button>
          <button onClick={handleDischarge} style={{ color: 'red' }}>
            X
          </button>
        </>
      )}
    </div>
  );
};

export default Bot;
