import React from 'react';

const Bot = ({ bot, onEnlist, onDelete, onShowSpecs, enlisted }) => {
  const { id, name, health, damage, armor, bot_class, catchphrase, avatar_url } = bot;

  const handleEnlist = () => {
    onEnlist(bot);
  };

  const handleDelete = () => {
    onDelete(id);
  };

  const handleShowSpecs = () => {
    onShowSpecs(bot);
  };

  return (
    <div className="bot-card">
      <img src={avatar_url} alt={name} />
      <h3>{name}</h3>
      <p>Health: {health}</p>
      <p>Damage: {damage}</p>
      <p>Armor: {armor}</p>
      <p>Class: {bot_class}</p>
      <p>Catchphrase: {catchphrase}</p>
      {enlisted ? (
        <button onClick={handleDelete}>Release</button>
      ) : (
        <button onClick={handleEnlist}>Enlist</button>
      )}
      <button onClick={handleShowSpecs}>Show Specs</button>
    </div>
  );
};

export default Bot;
