import React, { useState, useEffect } from 'react';
import BotCollection from './components/BotCollection';
import YourBotArmy from './components/YourBotArmy';

const App = () => {
  const [enlistedBots, setEnlistedBots] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8001/bots')
      .then((response) => response.json())
      .then((data) => setEnlistedBots(data))
      .catch((error) => console.error('Error fetching enlisted bots:', error));
  }, []);

  const handleEnlist = (bot) => {
    setEnlistedBots((prevEnlistedBots) => [...prevEnlistedBots, bot]);
  };

  const handleDelete = (botId) => {
    fetch(`http://localhost:8001/bots/${botId}`, {
      method: 'DELETE',
    })
      .then(() =>
        setEnlistedBots((prevEnlistedBots) =>
          prevEnlistedBots.filter((bot) => bot.id !== botId)
        )
      )
      .catch((error) => console.error('Error deleting bot:', error));
  };

  return (
    <div>
      <h1>Welcome to Bot Battlr</h1>
      <BotCollection onEnlist={handleEnlist} />
      <YourBotArmy enlistedBots={enlistedBots} onDelete={handleDelete} />
    </div>
  );
};

export default App;
