import React, { useState, useEffect } from 'react';
import BotsPage from './BotsPage';
import YourBotArmy from './YourBotArmy';
import BotCollection from './BotCollection'; // Assuming this is the correct import for BotCollection component.
import SortBar from './SortBar'; // Assuming this is the correct import for SortBar component.
import './App.css';

function App() {
  const [data, setData] = useState([]);
  const [enlistedBots, setEnlistedBots] = useState([]);


  useEffect(() => {
    fetch('http://localhost:8001/bots')
      .then((response) => response.json())
      .then((data) => setData(data))
      .catch((error) => console.error('Error fetching bots:', error));
  }, []);

  const handleEnlist = (bot) => {
    setEnlistedBots((prevEnlistedBots) => [...prevEnlistedBots, bot]);
  };

  const handleDelete = (botId) => {
    setEnlistedBots((prevEnlistedBots) =>
      prevEnlistedBots.filter((bot) => bot.id !== botId)
    );
  };

  return (
    <div>
      <h1>Welcome to Bot Battlr</h1>
      <SortBar data={data} setData={setData} />
      <BotCollection data={data} onEnlist={handleEnlist} />
      <YourBotArmy enlistedBots={enlistedBots} onDelete={handleDelete} />
    </div>
  );
}

export default App;
