// App.js
import React, { useState, useEffect } from 'react';
import YourBotArmy from './YourBotArmy';
import BotCollection from './BotCollection';
import SortBar from './SortBar';
import BotSpecs from './BotSpecs';
import './App.css';

function App() {
  const [data, setData] = useState([]);
  const [enlistedBots, setEnlistedBots] = useState([]);
  const [showBotSpecs, setShowBotSpecs] = useState(false);
  const [selectedBot, setSelectedBot] = useState(null);

  useEffect(() => {
    fetch('http://localhost:8001/bots')
      .then((response) => response.json())
      .then((data) => setData(data))
      .catch((error) => console.error('Error fetching bots:', error));
  }, []);

  // Rest of the functions remain the same...

  return (
    <div>
      <h1>Welcome to Bot Battlr</h1>
      {!showBotSpecs && <SortBar data={data} setData={setData} />}
      <YourBotArmy enlistedBots={enlistedBots} onDelete={handleDelete} />
      {showBotSpecs ? (
        <BotSpecs bot={selectedBot} onEnlist={handleEnlist} onBack={handleHideBotSpecs} />
      ) : (
        <BotCollection
          data={data}
          enlistedBots={enlistedBots}
          onEnlist={handleEnlist}
          onDelete={handleDelete}
          onShowBotSpecs={handleShowBotSpecs}
        />
      )}
    </div>
  );
}

export default App;
