import React, { useState, useEffect } from 'react';
import BotsPage from './BotsPage';
import YourBotArmy from './YourBotArmy';
import BotCollection from './BotCollection'; // Assuming this is the correct import for BotCollection component.
import SortBar from './SortBar'; // Assuming this is the correct import for SortBar component.
import css from './App.css';

function App() {
  const [data, setData, onEnlist] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8001/bots')
      .then((response) => response.json())
      .then((data) => setData(data))
      .catch((error) => console.error('Error fetching bots:', error));
  }, []);

  return (
    <div className='bot-specs'>
      <SortBar data={data} setData={setData} />
      {data.length > 0 ? ( // Check if data has been fetched and is not empty
        <BotCollection data={data} onEnlist={onEnlist} />
      ) : (
        <p>Loading...</p> // You can display a loading message or a spinner while data is being fetched
      )}
      <YourBotArmy enlistedBots={data} />
    </div>
  );
}

export default App;
