// YourBotArmy.js
import React from 'react';
import Bot from './Bot';

const YourBotArmy = ({ enlistedBots = [], onDelete, onDischarge }) => {
  const handleDischarge = (botId) => {
    // Call the onDischarge function to remove the bot from the frontend (YourBotArmy)
    onDischarge(botId);
  };

  return (
    <div className="your-bot-army">
      <h3>Your Bot Army</h3>
      {enlistedBots.length === 0 ? (
        <p>No bots enlisted in your army.</p>
      ) : (
        <div className="bot-card-container">
          {enlistedBots.map((bot) => (
            <Bot
              key={bot.id}
              bot={bot}
              onEnlist={() => {}}
              onDelete={onDelete}
              onShowSpecs={() => {}}
              onDischarge={handleDischarge} // Pass the handleDischarge function to Bot component
              enlisted
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default YourBotArmy;
